--
--	Create fn_HexToLong
--
CREATE
FUNCTION	[dbo].[fn_HexToLong]
			(
				@Hex		VARCHAR(8),
				@Reverse	BIT
			)
RETURNS		INT
BEGIN

	IF (@Reverse = 1)
	BEGIN
		SET @Hex = Substring(@Hex, 7, 2) + Substring(@Hex, 5, 2) + Substring(@Hex, 3, 2) + Substring(@Hex, 1, 2)
	END

	RETURN CONVERT(INT, CONVERT(VARBINARY(4), @Hex, 2))

END
