			IF ( @lastTimeStamp is NULL) OR ( @TimeStamp < DateAdd(hour, 24, GetDate()) AND @Timestamp > @lastTimeStamp )
			BEGIN
				UPDATE MTD_LastPositions WITH (ROWLOCK) SET PositionRecID = @RecID, DemographicID=@DemographicID, DeviceID=@DeviceID,
