                    WHERE '
                    SET @sSQL = @sSql  + '(UTCTimeStamp <= ''' +
                        Convert(varchar(30), @TimeIn, 109) + ''') AND  ServerName in (''' + @ServerNameList + ''') 
                                 ORDER BY UTCTimeStamp DESC ' 
      
       --PRINT @sSQL    

      EXEC(@sSQL)
END 
          
          -- Convert(varchar(30), @TimeIn, 100)




