		m.CreateTime,
		m.SendTime,
		m.ReplyTime,
		m.APN,
		m.NetworkUserId,
		m.NetworkPassword,
		m.SendMethod,
		m.EncryptType,
		m.MessageType,
		m.IPAddress,
		m.Port,
		m.CSD,
		m.LL,
		m.DemographicId,
		m.EmailFormat,
		m.SMSMessageServiceFetchDate,
		m.SMSMessageServiceBatchId,
		m.SMSMessageServiceHostName,
		m.DispatchAttempts,
		m.SendAttempts,
		m.[Status],
		m.StartedSend,
		m.CompletedSend,
		m.Response,
		m.ExceptionMessage,
		m.QueuedDate
	FROM 
		MTD_SMSMessages m
	WHERE 
		SMSMessageServiceBatchId = @SMSMessageServiceBatchId
